### app/tldr/page.tsx
```typescript
import { TldrHero } from "@/components/tldr/hero";
import { TldrToolGrid } from "@/components/tldr/tool-grid";
import { TldrSynergyDiagram } from "@/components/tldr/synergy-diagram";
import { tldrTools, tldrCategories } from "@/lib/tldr-data";
import { SectionShell } from "@/components/section-shell";

export default function TldrPage() {
  const primaryTools = tldrTools.filter(t => t.category === 'primary');
  const supportingTools = tldrTools.filter(t => t.category === 'supporting');

  return (
    <main>
      <TldrHero />

      <SectionShell id="synergies" title="How It All Connects">
        <TldrSynergyDiagram tools={tldrTools} />
