# Task: Test Utilities and Logging Infrastructure

## Purpose
Create shared test utilities, custom matchers, and comprehensive logging
infrastructure that all /tldr tests will use.

## Files to Create

### test-utils/tldr-test-helpers.ts
```typescript
import { render, RenderOptions } from '@testing-library/react';
import { ReactElement } from 'react';

// ============================================================
// LOGGING UTILITIES
// ============================================================

const LOG_LEVELS = {
  DEBUG: 0,
  INFO: 1,
  WARN: 2,
  ERROR: 3
} as const;

let currentLogLevel = LOG_LEVELS.DEBUG;

export const setLogLevel = (level: keyof typeof LOG_LEVELS) => {
  currentLogLevel = LOG_LEVELS[level];
};

export const testLog = {
  debug: (msg: string, data?: unknown) => {
    if (currentLogLevel <= LOG_LEVELS.DEBUG) {
      console.log(`[DEBUG] ${msg}`, data ?? '');
    }
  },
  info: (msg: string, data?: unknown) => {
    if (currentLogLevel <= LOG_LEVELS.INFO) {
      console.log(`[INFO] ${msg}`, data ?? '');
    }
  },
  warn: (msg: string, data?: unknown) => {
    if (currentLogLevel <= LOG_LEVELS.WARN) {
      console.warn(`[WARN] ${msg}`, data ?? '');
    }
  },
  error: (msg: string, data?: unknown) => {
    console.error(`[ERROR] ${msg}`, data ?? '');
  },
  timing: (label: string, startTime: number) => {
    const duration = performance.now() - startTime;
    console.log(`[TIMING] ${label}: ${duration.toFixed(2)}ms`);
    return duration;
  },
  dom: (element: HTMLElement) => {
    console.log('[DOM]', element.outerHTML.substring(0, 500));
  }
};

// ============================================================
// RENDER UTILITIES
// ============================================================

interface ExtendedRenderOptions extends RenderOptions {
  testName?: string;
  logDom?: boolean;
}

export const renderWithLogging = (
  ui: ReactElement,
  options: ExtendedRenderOptions = {}
) => {
  const { testName = 'Unknown Test', logDom = false, ...renderOptions } = options;

  testLog.info(`Rendering component for: ${testName}`);
  const startTime = performance.now();

  const result = render(ui, renderOptions);

  testLog.timing(`Render complete: ${testName}`, startTime);

  if (logDom) {
    testLog.dom(result.container as HTMLElement);
  }

  return {
    ...result,
    logDom: () => testLog.dom(result.container as HTMLElement)
  };
};

// ============================================================
// MOCK DATA FACTORIES
// ============================================================

export const createMockTool = (overrides?: Partial<TldrTool>): TldrTool => ({
  id: 'mock-tool',
  name: 'Mock Tool',
  shortName: 'MT',
  tagline: 'A mock tool for testing',
  category: 'primary',
  language: 'TypeScript',
  github: 'https://github.com/test/mock-tool',
  stars: 100,
  icon: 'Box',
  color: 'from-blue-500 to-cyan-500',
  whatItDoes: 'This mock tool does testing things.',
  whyUseful: 'It helps test other components effectively.',
  implementation: ['TypeScript', 'React', 'Testing Library'],
  synergies: [],
  ...overrides
});

export const createMockTools = (count: number): TldrTool[] => {
  return Array.from({ length: count }, (_, i) => 
    createMockTool({
      id: `mock-tool-${i}`,
      name: `Mock Tool ${i}`,
      shortName: `MT${i}`
    })
  );
};

// ============================================================
// CUSTOM MATCHERS
// ============================================================

expect.extend({
  toBeAccessible(received: HTMLElement) {
    // Basic accessibility checks
    const hasRole = received.hasAttribute('role');
    const hasAriaLabel = received.hasAttribute('aria-label') || 
                         received.hasAttribute('aria-labelledby');

    testLog.debug('Accessibility check', { hasRole, hasAriaLabel });

    const pass = hasRole || hasAriaLabel || received.tagName === 'BUTTON';

    return {
      pass,
      message: () => pass 
        ? `Expected element not to be accessible`
        : `Expected element to have ARIA attributes. Element: ${received.outerHTML.substring(0, 100)}`
    };
  },

  toHaveAnimationDisabled(received: HTMLElement) {
    // Check for reduced motion handling
    const styles = window.getComputedStyle(received);
    const hasNoAnimation = styles.animation === 'none' || 
                          styles.transition === 'none';

    testLog.debug('Animation check', { hasNoAnimation });

    return {
      pass: hasNoAnimation,
      message: () => `Expected element to have animations disabled`
    };
  }
});

// ============================================================
// WAIT UTILITIES
// ============================================================

export const waitForAnimation = async (ms: number = 300) => {
  testLog.debug(`Waiting ${ms}ms for animation`);
  await new Promise(resolve => setTimeout(resolve, ms));
};

// ============================================================
// MOCK REDUCED MOTION
// ============================================================

export const mockReducedMotion = (reduced: boolean) => {
  testLog.info(`Mocking prefers-reduced-motion: ${reduced}`);

  Object.defineProperty(window, 'matchMedia', {
    writable: true,
    value: jest.fn().mockImplementation(query => ({
      matches: query === '(prefers-reduced-motion: reduce)' ? reduced : false,
      media: query,
      onchange: null,
      addListener: jest.fn(),
      removeListener: jest.fn(),
      addEventListener: jest.fn(),
      removeEventListener: jest.fn(),
      dispatchEvent: jest.fn(),
    })),
  });
};
```

