# E2E Tests for Search/Filter Functionality

## Overview
Comprehensive Playwright tests for the new tool search and filter feature, covering all user interactions and edge cases.

## Test File: `tests/e2e/tldr-search.spec.ts`

```typescript
import { test, expect } from "@playwright/test";

test.describe("TL;DR Search Functionality", () => {
  test.beforeEach(async ({ page }) => {
    console.log('[E2E:Search] Navigating to /tldr');
    await page.goto("/tldr");
    console.log('[E2E:Search] Page loaded');
  });

  test.describe("Search Bar Rendering", () => {
    test("should display search bar above tool grid", async ({ page }) => {
      console.log('[E2E:Search] Checking search bar visibility');

      const searchInput = page.getByPlaceholder(/search tools/i);
      await expect(searchInput).toBeVisible();
      console.log('[E2E:Search] Search input found');

      // Should be positioned above the tool grid
      const searchBox = await searchInput.boundingBox();
      const gridSection = page.locator('section').filter({ has: page.getByText('Core Flywheel Tools') });
      const gridBox = await gridSection.boundingBox();

      if (searchBox && gridBox) {
        console.log(`[E2E:Search] Search Y: ${searchBox.y}, Grid Y: ${gridBox.y}`);
        expect(searchBox.y).toBeLessThan(gridBox.y);
      }

      console.log('[E2E:Search] Search bar position verified');
    });

    test("should have proper accessibility attributes", async ({ page }) => {
      console.log('[E2E:Search] Checking accessibility attributes');

      const searchInput = page.getByRole('searchbox');
      await expect(searchInput).toHaveAttribute('aria-label', /search/i);

      console.log('[E2E:Search] Accessibility attributes verified');
    });
  });

  test.describe("Search Behavior", () => {
    test("should filter tools as user types", async ({ page }) => {
      console.log('[E2E:Search] Testing live filtering');

      const searchInput = page.getByPlaceholder(/search tools/i);

      // Get initial tool count
      const initialCards = page.locator('[data-testid="tool-card"]');
      const initialCount = await initialCards.count();
      console.log(`[E2E:Search] Initial tool count: ${initialCount}`);

      // Type search query
      console.log('[E2E:Search] Typing "memory"');
      await searchInput.fill('memory');

      // Wait for filtering
      await page.waitForTimeout(200); // Debounce delay

      // Check filtered results
      const filteredCards = page.locator('[data-testid="tool-card"]:visible');
      const filteredCount = await filteredCards.count();
      console.log(`[E2E:Search] Filtered tool count: ${filteredCount}`);

      expect(filteredCount).toBeLessThan(initialCount);
      expect(filteredCount).toBeGreaterThan(0);

      // Verify CM (CASS Memory) is visible
      await expect(page.getByText('CM')).toBeVisible();
      console.log('[E2E:Search] Memory search successful');
    });

    test("should search across name, description, and tech stack", async ({ page }) => {
      console.log('[E2E:Search] Testing multi-field search');

      const searchInput = page.getByPlaceholder(/search tools/i);

      // Search by tech stack
      console.log('[E2E:Search] Searching "Rust"');
      await searchInput.fill('Rust');
      await page.waitForTimeout(200);

      // CASS and XF are Rust tools
      await expect(page.getByText('CASS').first()).toBeVisible();
      console.log('[E2E:Search] Rust search found CASS');

      // Clear and search by description keyword
      console.log('[E2E:Search] Clearing and searching "tmux"');
      await searchInput.clear();
      await searchInput.fill('tmux');
      await page.waitForTimeout(200);

      // NTM and SLB use tmux
      await expect(page.getByText('NTM')).toBeVisible();
      console.log('[E2E:Search] Tmux search found NTM');
    });

    test("should show no results state for non-matching query", async ({ page }) => {
      console.log('[E2E:Search] Testing no results state');

      const searchInput = page.getByPlaceholder(/search tools/i);
      await searchInput.fill('xyznonexistent123');
      await page.waitForTimeout(200);

      console.log('[E2E:Search] Checking for no results message');
      await expect(page.getByText(/no tools match/i)).toBeVisible();

      console.log('[E2E:Search] No results state verified');
    });

    test("should show results count", async ({ page }) => {
      console.log('[E2E:Search] Testing results count display');

      const searchInput = page.getByPlaceholder(/search tools/i);
      await searchInput.fill('agent');
      await page.waitForTimeout(200);

      // Should show "X of 13 tools" or similar
      const countDisplay = page.locator('text=/\\d+ of \\d+ tools/i');
      await expect(countDisplay).toBeVisible();

      console.log('[E2E:Search] Results count displayed');
    });
  });

  test.describe("Clear and Reset", () => {
    test("should have clear button when search has input", async ({ page }) => {
      console.log('[E2E:Search] Testing clear button');

      const searchInput = page.getByPlaceholder(/search tools/i);

      // Clear button should not be visible initially
      const clearButton = page.getByRole('button', { name: /clear/i });
      await expect(clearButton).not.toBeVisible();

      // Type something
      await searchInput.fill('test');

      // Clear button should appear
      await expect(clearButton).toBeVisible();
      console.log('[E2E:Search] Clear button appeared');

      // Click clear
      await clearButton.click();

      // Input should be empty, all tools visible
      await expect(searchInput).toHaveValue('');
      console.log('[E2E:Search] Search cleared');
    });

    test("should clear search on Escape key", async ({ page }) => {
      console.log('[E2E:Search] Testing Escape key');

      const searchInput = page.getByPlaceholder(/search tools/i);
      await searchInput.fill('test');

      await page.keyboard.press('Escape');

      await expect(searchInput).toHaveValue('');
      console.log('[E2E:Search] Escape cleared search');
    });
  });

  test.describe("Keyboard Shortcuts", () => {
    test("should focus search on / key press", async ({ page }) => {
      console.log('[E2E:Search] Testing / shortcut');

      // Press / anywhere on page
      await page.keyboard.press('/');

      // Search should be focused
      const searchInput = page.getByPlaceholder(/search tools/i);
      await expect(searchInput).toBeFocused();

      console.log('[E2E:Search] / shortcut works');
    });

    test("should not trigger / shortcut when already in input", async ({ page }) => {
      console.log('[E2E:Search] Testing / in input does not re-focus');

      const searchInput = page.getByPlaceholder(/search tools/i);
      await searchInput.focus();
      await searchInput.fill('test');

      // Type / should add to input, not clear it
      await page.keyboard.press('/');

      await expect(searchInput).toHaveValue('test/');
      console.log('[E2E:Search] / in input behaves correctly');
    });
  });

  test.describe("Mobile Behavior", () => {
    test("should work on mobile viewport", async ({ page }) => {
      console.log('[E2E:Search] Testing mobile search');

      await page.setViewportSize({ width: 375, height: 667 });
      await page.reload();

      const searchInput = page.getByPlaceholder(/search tools/i);
      await expect(searchInput).toBeVisible();

      // Touch target should be adequate
      const box = await searchInput.boundingBox();
      if (box) {
