  showDragHandle = true,
  closeOnBackdrop = true,
  closeOnEscape = true,
  onOpenComplete,
  contentClassName,
}: BottomSheetProps) {
  const sheetRef = useRef<HTMLDivElement>(null);
  const dragControls = useDragControls();
  const prefersReducedMotion = useReducedMotion();

  // Handle Escape key
  useEffect(() => {
    if (!closeOnEscape || !isOpen) return;

    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.key === "Escape") {
        console.log("[BottomSheet] Escape pressed, closing");
        onClose();
      }
    };

    document.addEventListener("keydown", handleKeyDown);
    return () => document.removeEventListener("keydown", handleKeyDown);
  }, [isOpen, closeOnEscape, onClose]);

  // Trap focus within sheet
  useEffect(() => {
    if (!isOpen || !sheetRef.current) return;

    const sheet = sheetRef.current;
    const focusableElements = sheet.querySelectorAll(
      'button, [href], input, select, textarea, [tabindex]:not([tabindex="-1"])'
    );
    const firstElement = focusableElements[0] as HTMLElement;
    const lastElement = focusableElements[focusableElements.length - 1] as HTMLElement;

    // Focus first element
    firstElement?.focus();

    const handleTab = (e: KeyboardEvent) => {
      if (e.key !== "Tab") return;

      if (e.shiftKey) {
        if (document.activeElement === firstElement) {
          e.preventDefault();
          lastElement?.focus();
        }
      } else {
        if (document.activeElement === lastElement) {
          e.preventDefault();
          firstElement?.focus();
        }
      }
    };

    sheet.addEventListener("keydown", handleTab);
    return () => sheet.removeEventListener("keydown", handleTab);
  }, [isOpen]);

  // Lock body scroll when open
  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "";
    }
    return () => {
      document.body.style.overflow = "";
    };
  }, [isOpen]);

  const handleDragEnd = useCallback((event: MouseEvent | TouchEvent | PointerEvent, info: PanInfo) => {
    console.log(`[BottomSheet] Drag ended: offsetY=${info.offset.y}, velocityY=${info.velocity.y}`);

    // Close if dragged down past threshold or with sufficient velocity
    if (info.offset.y > 100 || info.velocity.y > 500) {
      console.log("[BottomSheet] Closing due to swipe");
      onClose();
    }
  }, [onClose]);

  const handleBackdropClick = useCallback(() => {
    if (closeOnBackdrop) {
      console.log("[BottomSheet] Backdrop clicked, closing");
      onClose();
    }
  }, [closeOnBackdrop, onClose]);

  return (
    <AnimatePresence>
      {isOpen && (
        <>
          {/* Backdrop */}
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: prefersReducedMotion ? 0 : 0.2 }}
            onClick={handleBackdropClick}
            className="fixed inset-0 z-50 bg-black/60 backdrop-blur-sm"
            data-testid="sheet-backdrop"
            aria-hidden="true"
          />

          {/* Sheet */}
          <motion.div
            ref={sheetRef}
            role="dialog"
            aria-modal="true"
            aria-label={title || "Bottom sheet"}
            data-testid="bottom-sheet"
            initial={{ y: "100%" }}
            animate={{ y: 0 }}
            exit={{ y: "100%" }}
            transition={
              prefersReducedMotion
                ? { duration: 0 }
                : { type: "spring", damping: 25, stiffness: 300 }
            }
            drag="y"
            dragControls={dragControls}
            dragConstraints={{ top: 0, bottom: 0 }}
            dragElastic={{ top: 0, bottom: 0.5 }}
            onDragEnd={handleDragEnd}
            onAnimationComplete={() => {
              console.log("[BottomSheet] Animation complete");
              onOpenComplete?.();
            }}
            className={cn(
              "fixed bottom-0 left-0 right-0 z-50",
              "rounded-t-3xl bg-slate-900",
              "border-t border-white/10",
              "shadow-2xl shadow-black/50",
              "flex flex-col",
            )}
            style={{ maxHeight: `${maxHeight}vh` }}
          >
            {/* Drag Handle */}
            {showDragHandle && (
              <div
                className="sticky top-0 z-10 bg-slate-900 pt-3 pb-2 cursor-grab active:cursor-grabbing"
                onPointerDown={(e) => dragControls.start(e)}
                data-testid="drag-handle"
              >
                <div className="mx-auto h-1 w-12 rounded-full bg-slate-700" />
              </div>
            )}

            {/* Header with title and close button */}
            {title && (
              <div className="flex items-center justify-between px-6 py-3 border-b border-white/5">
                <h2 className="text-lg font-semibold text-white">{title}</h2>
                <button
                  onClick={onClose}
                  className="p-2 -mr-2 text-slate-400 hover:text-white transition-colors"
                  aria-label="Close"
                >
                  <X className="h-5 w-5" />
                </button>
              </div>
            )}

            {/* Close button (when no title) */}
            {!title && (
              <button
                onClick={onClose}
                className="absolute top-4 right-4 p-2 text-slate-400 hover:text-white transition-colors z-10"
                aria-label="Close"
              >
                <X className="h-5 w-5" />
              </button>
            )}

            {/* Content */}
            <div
              className={cn(
                "flex-1 overflow-y-auto overscroll-contain",
                "px-6 pb-8",
                !title && "pt-4",
                contentClassName
              )}
              data-testid="sheet-content"
            >
              {children}
            </div>

            {/* Safe area padding for iOS */}
            <div className="pb-[env(safe-area-inset-bottom)]" />
          </motion.div>
        </>
      )}
    </AnimatePresence>
  );
}
```

