## Grid-Aware Navigation
```tsx
// Calculate grid position
const getGridPosition = (index: number, cols: number) => ({
  row: Math.floor(index / cols),
  col: index % cols,
});

// Navigate respecting grid layout
const navigateGrid = (direction: 'up' | 'down' | 'left' | 'right') => {
  const { row, col } = getGridPosition(focusedIndex, cols);
  switch (direction) {
    case 'up': return focusTool((row - 1) * cols + col);
    case 'down': return focusTool((row + 1) * cols + col);
    case 'left': return focusTool(focusedIndex - 1);
    case 'right': return focusTool(focusedIndex + 1);
  }
};
